import React from "react";

import { v4 as uuidv4 } from "uuid"


import directory from "./directory"
import file from "./file"


class Commands extends React.Component {
    state = {
        title: "",
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    listenCmd = event => {

        if (event.key === "Enter") {
            if (this.state.title.split(" ")[0] === "ls") {
                this.ls()
            }
            else if (this.state.title.split(" ")[0] === "history") {
                this.history()
            }else if (this.state.title.split(" ")[0] === "pwd") {
                this.pwd()
            } else {
                var d = this.props.prompt + " " + this.state.title;
                this.props.addCommandPops(d)
            }
            this.setState({
                command: this.state.title,
                title: ""
            })
        }
    }
    //commands
    ls = () => {

        var d =this.props.prompt + " " + this.state.title +"\n"

        directory.map(dir => {
            if (dir.path === this.props.path) {
                d += dir.name + "     ";
            }
        })

        d += "\n"
        file.map(file => {
            if (file.path === this.props.path) {
                d += file.name + "      ";
            }
        })
        console.log(d)
        this.props.addCommandPops(d)

    }
    history = () =>{
        var d =this.props.prompt + " " + this.state.title +"\n"
        var i=0;
        this.props.history.map(his=> {
            if(this.props.history.length-1 === i ){
                d += his.title 
            }else{
                d += his.title + "\n"
            }
            i++;
        })
        this.props.addCommandPops(d)
    }
    pwd = () =>{
        var d =this.props.prompt + " " + this.state.title +"\n"
        d +=this.props.path
        this.props.addCommandPops(d)
    }
    cd 
    render() {
        return (
                <div className="lineCmd">
                    <span>
                        {this.props.prompt}
                    </span>
                    <input type="text" className="inputCmd"
                        value={this.state.title}
                        name="title"
                        onChange={this.onChange}
                        onKeyDown={e => {
                            if (e.key === "Enter") {
                                this.listenCmd(e)
                                this.props.addHistoryProps(this.state.title)
                            }
                        }}>
                    </input>
                </div>
        )
    }
}


export default Commands


