import React from "react";

import directory from "./directory"
import file from "./file"


class Commands extends React.Component {
    state = {
        title: "",
        command: []
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    listenCmd = event => {

        if (event.key === "Enter") {

            if (this.state.title.split(" ")[0] === "ls") {
                this.ls()
            } else {
                var d = this.state.completePrompt + " " + this.state.title;
                this.addToHistory(this.state.title, d);
            }
            this.setState({
                command: this.state.title,
                title: ""
            })
        }

    }
    //commands
    ls = () => {
        var d = this.state.completePrompt + " " + this.state.title + "\n"
        directory.map(dir => {
            if (dir.path === this.state.path) {
                d += dir.name + " ";
            }
        })
        d += "\n"
        file.map(file => {
            if (file.path === this.state.path) {
                d += file.name + " ";
            }
        })
        d += "\n"
        this.addToHistory(this.state.title, d)

    }
    render() {
        return (
            <div className="output">
                {/* <ul>
                {this.state.text.split("\n").map(
                    his => <li key={uuidv4()}>
                        <span>{his}</span>
                    </li>
                )}
            </ul> */}
                {/* <span>Hello</span> */}
                <div className="lineCmd">
                    <span>
                        {this.props.prompt}
                    </span>
                    <input type="text" className="inputCmd"
                        value={this.state.title}
                        name="title"
                        onChange={this.onChange}
                        // {this.props.addToHistoryProps(this.state.title)}
                        onKeyDown={e => {this.listenCmd(e) 
                            e.key==="Enter" ?  this.listenCmd(e) :this.props.addToHistoryProps(this.state.title)
                        }}>
                    </input>
                </div>
            </div>
        )
    }
}


export default Commands


