import React from "react";

import { v4 as uuidv4 } from "uuid"


import directory from "./directory"
import file from "./file"



class Commands extends React.Component {
    state = {
        title: "",
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    listenCmd = event => {

        if (event.key === "Enter") {
            if (this.state.title.split(" ")[0] === "ls") {
                this.ls()
            }
            else if (this.state.title.split(" ")[0] === "history") {
                this.history()
            } else if (this.state.title.split(" ")[0] === "pwd") {
                this.pwd()
            } else if (this.state.title.split(" ")[0] === "cd") {
                this.cd()
            } else if (this.state.title.split(" ")[0] === "touch") {
                this.touch()
            } else {
                var d = this.props.prompt + " " + this.state.title;
                this.props.addCommandPops(d)
            }
            this.setState({
                command: this.state.title,
                title: ""
            })
        }
    }
    //commands
    ls = () => {

        var d = this.props.prompt + " " + this.state.title + "\n"

        directory.map(dir => {
            if (dir.path === this.props.path) {
                d += dir.name + "     ";
            }
        })

        d += "\n"
        file.map(file => {
            if (file.path === this.props.path) {
                d += file.name + "      ";
            }
        })
        console.log(d)
        this.props.addCommandPops(d)

    }
    history = () => {
        var d = this.props.prompt + " " + this.state.title + "\n"
        var i = 0;
        this.props.history.map(his => {
            if (this.props.history.length - 1 === i) {
                d += his.title
            } else {
                d += his.title + "\n"
            }
            i++;
        })
        this.props.addCommandPops(d)
    }
    pwd = () => {
        var d = this.props.prompt + " " + this.state.title
        d += this.props.path
        this.props.addCommandPops(d)
    }
    cd = () => {
        var d = this.props.prompt + " " + this.state.title
        if (this.state.title.split(" ")[0] === "..") {
            this.props.changePathProp("/home/user")
        } else if (this.state.title.split(" ")[0] === "-") {
            this.props.changePathProp(this.props.prevPath)
        } else {
            directory.map(dir => {
                if (dir.name === this.state.title.split(" ")[1] && dir.path === this.props.path) {
                    this.props.changePathProp(this.props.path + "/" + dir.name)
                }
            })
        }
        this.props.addCommandPops(d)
    }
    touch = () => {
        var d = this.props.prompt + " " + this.state.title
        var addFile = true
        file.map(f =>{
            if(this.props.path === f.path && this.state.title.split(" ")[1] === f.name){
                addFile = false
            }
        })
        if (this.state.title.split(" ")[1] !== "" && addFile===true) {

            const newFile = {
                name: this.state.title.split(" ")[1],
                permissions: "",
                path: this.props.path,
            }

            file.push(newFile)
        }
        // file.map(f =>{
        //     console.log(f.name)
        // })
        this.props.addCommandPops(d)
    }
    m = () => {
        var d = this.props.prompt + " " + this.state.title
        if (this.state.title.split(" ")[0] === "..") {
            this.props.changePathProp("/home/user")
        } else if (this.state.title.split(" ")[0] === "-") {
            this.props.changePathProp(this.props.prevPath)
        } else {
            directory.map(dir => {
                if (dir.name === this.state.title.split(" ")[1] && dir.path === this.props.path) {
                    this.props.changePathProp(this.props.path + "/" + dir.name)
                }
            })
        }
        this.props.addCommandPops(d)
    }
    render() {

        return (
            <div className="lineCmd">
                <span>
                    {this.props.prompt}
                </span>
                <input type="text" className="inputCmd"
                    value={this.state.title}
                    name="title"
                    onChange={this.onChange}
                    onKeyDown={e => {
                        if (e.key === "Enter") {
                            this.listenCmd(e)
                            this.props.addHistoryProps(this.state.title)
                        }
                    }}>
                </input>
            </div>
        )
    }
}


export default Commands


