import React from "react";

import { v4 as uuidv4 } from "uuid"


import directory from "./directory"
import file from "./file"


class Commands extends React.Component {
    state = {
        title: "",
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    listenCmd = event => {

        if (event.key === "Enter") {
            if (this.state.title.split(" ")[0] === "ls") {
                this.ls()
            } else {
                var d = this.props.prompt + " " + this.state.title;
                this.props.addCommandPops(d)
            }
            this.setState({
                command: this.state.title,
                title: ""
            })
        }
        this.props.command.map(cmd => {
            console.log(cmd.text + " ++++++++++hello+++++++++ ")
        }
        )
    }
    //commands
    ls = () => {
        var d = this.state.completePrompt + " " + this.state.title + "\n"
        directory.map(dir => {
            if (dir.path === this.state.path) {
                d += dir.name + " ";
            }
        })
        d += "\n"
        file.map(file => {
            if (file.path === this.state.path) {
                d += file.name + " ";
            }
        })
        d += "\n"
        // this.addToHistory(this.state.title, d)

    }
    render() {
        return (
            <div className="output">
                <ul>
                    {this.props.command.map(cmd => {
                        <li key={cmd.id}>
                            <span>{cmd.text}</span>
                            alert
                        </li>
                    })}
                </ul>
                <div className="lineCmd">
                    <span>
                        {this.props.prompt}
                    </span>
                    <input type="text" className="inputCmd"
                        value={this.state.title}
                        name="title"
                        onChange={this.onChange}
                        onKeyDown={e => {
                            if (e.key === "Enter") {
                                this.listenCmd(e)
                                this.props.addHistoryProps(this.state.title)

                            }
                        }}>
                    </input>
                </div>
            </div>
        )
    }
}


export default Commands


