import React from "react";

import { v4 as uuidv4 } from "uuid"
import directory from "./directory"
import file from "./file"

class Terminal extends React.Component {
    state = {
        completePrompt: "user@localhost:~#",
        title: "",
        command: "",
        path: "/home/user",
        history: [],
        text: "",
        allHistory: []
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    addToHistory = (title,text) => {
        const newHistory = {
            id: uuidv4(),
            title: title
        }
        this.setState({
            history: [...this.state.history, newHistory],
            allHistory: [...this.state.history, newHistory],
            text: this.state.text.concat(text)//this.state.text + this.state.completePrompt + " " + title + "\n"
        })
    }

    listenCmd = event => {
        if (event.key === "Enter") {

            if(this.state.title==="ls"){
                this.ls()
            }else{
                let d =this.state.completePrompt + " " + this.state.title;
                this.addToHistory(this.state.title,d);
            }
            this.setState({
                command: this.state.title,
                title: ""
            })
        }

    }
    //commands
    ls = () => {
        let d =this.state.completePrompt + " " + this.state.title + "\n"
        directory.map(dir =>{
            if(dir.path === this.state.path){
                d+=dir.name+"\n";
            }
        })
        file.map(file =>{
            if(file.path === this.state.path){
                d+=file.name+"\n";
            }
        })
        this.addToHistory(this.state.title,d)  

    }

    render() {
        return (
            <div className="termContainer">
                <div className="output">
                    <ul>
                        {this.state.text.split("\n").map(
                            his => <li key={uuidv4()}>
                                <span>{his}</span>
                            </li>
                        )}
                    </ul>
                </div>
                <div className="lineCmd">
                    <span>
                        {this.state.completePrompt}
                    </span>
                    <input type="text" className="inputCmd"
                        value={this.state.title}
                        name="title"
                        onChange={this.onChange}
                        onKeyDown={this.listenCmd}>
                    </input>
                </div>
            </div>
        )
    }
}


export default Terminal




