import React from "react";

import { v4 as uuidv4 } from "uuid"
import Commands from "./Commands"

class Terminal extends React.Component {
    state = {
        completePrompt: "user@localhost:~#",
        title: "",
        command: "",
        path: "/home/user",
        history: [],
        text: "",
        allHistory: []
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    addToHistory = title => {
        const newHistory = {
            id: uuidv4(),
            title: title
        }
        this.setState({
            history: [...this.state.history, newHistory],
            allHistory: [...this.state.history, newHistory],
            text: this.state.text+this.state.completePrompt + " " +title+ "\n"
        })
    }
    listenCmd = event => {
        if (event.key === "Enter") {
            this.addToHistory(this.state.title)
            this.setState({
                command: this.state.title,
                title: ""
            })
        }
    }

    render() {


        return (
            <div className="termContainer">
                 <div className="output" >
                    <ul>
                        {this.state.text.split("\n").map(
                            his => <li key={his}>
                                <span>{his}</span>
                            </li>
                        )}
                        {/* <Commands command={this.state.command} prompt={this.state.completePrompt}/> */}
                    </ul>
                </div>
                <div className="lineCmd">
                    <span>
                        {this.state.completePrompt}
                    </span>
                    <input type="text" className="inputCmd"
                        value={this.state.title}
                        name="title"
                        onChange={this.onChange}
                        onKeyDown={this.listenCmd}>
                    </input>
                </div>
            </div>
        )
    }
}


export default Terminal




