import React from "react";


class Terminal extends React.Component {
    state = {
        title: "",
    }
    handleSubmit= e =>{
        e.preventDefault();
        if(this.state.title.trim()){
            // addtohistory
            this.setState({
                title:"",
            })
        }
    }
    render() {
        return (
            <div className="termContainer">
                <div className="inputLine">
                    <form onSub className="cmdLine">
                        <p>user</p>
                        <input type="text" className="inputCmd"></input>
                    </form>
                </div>
            </div>
        )
    }
}


export default Terminal
