import React from "react";

import { v4 as uuidv4 } from "uuid"


import directory from "./directory"
import file from "./file"


class Commands extends React.Component {
    state = {
        title: "",
        prompt:"",
        command: []
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    addCommand = (text) => {
        const newCommand = {
            id: uuidv4(),
            text: text
        }
        this.setState({
            command: [...this.state.command, newCommand],
            // text: this.state.text.concat(text)//this.state.text + this.state.completePrompt + " " + title + "\n"
        })
        console.log(text+" ++++++++++hello+++++++++ ")
    }

    listenCmd = event => {

        if (event.key === "Enter") {
            if (this.state.title.split(" ")[0] === "ls") {
                this.ls()
            } else {
                var d = this.state.completePrompt + " " + this.state.title;
                // this.addToHistory(this.state.title, d);
                this.addCommand(d)
            }
            this.setState({
                command: this.state.title,
                title: ""
            })
        }

    }
    //commands
    ls = () => {
        var d = this.state.completePrompt + " " + this.state.title + "\n"
        directory.map(dir => {
            if (dir.path === this.state.path) {
                d += dir.name + " ";
            }
        })
        d += "\n"
        file.map(file => {
            if (file.path === this.state.path) {
                d += file.name + " ";
            }
        })
        d += "\n"
        // this.addToHistory(this.state.title, d)

    }
    render() {
        return (
            <div className="output">
                <ul>
                {/* {this.state.command.map(
                    cmd => <li key={cmd.id}>
                        <span>{cmd.text}</span>
                    </li>
                )} */}
            </ul>
                <div className="lineCmd">
                    <span>
                        {this.props.prompt}
                    </span>
                    <input type="text" className="inputCmd"
                        value={this.state.title}
                        name="title"
                        onChange={this.onChange}
                        onKeyDown={e => {
                            this.listenCmd(e)
                            if (e.key === "Enter") { this.props.addToHistoryProps(this.state.title) 

                            }
                        }}>
                    </input>
                </div>
            </div>
        )
    }
}


export default Commands


