import React from "react";

import { v4 as uuidv4 } from "uuid"
import Commands from "./Commands";

class Terminal extends React.Component {
    state = {
        prompt: "user@localhost:~#",
        path: "/home/user",
        history: [],
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    addToHistory = (title) => {
        const newHistory = {
            id: uuidv4(),
            title: title
        }
        this.setState({
            history: [...this.state.history, newHistory],
            // text: this.state.text.concat(text)//this.state.text + this.state.completePrompt + " " + title + "\n"
        })
        console.log(title+" hello ")
    }

    render() {
        return (
            <div className="termContainer">
                <Commands addToHistoryProps={this.addToHistory} prompt={this.state.prompt} path={this.state.path}/>
            </div>
        )
    }
}


export default Terminal
