import React from "react";

import { v4 as uuidv4 } from "uuid"
import Commands from "./Commands";

class Terminal extends React.Component {
    state = {
        prompt: "user@localhost:~#",
        path: "/home/user",
        history: [],
        command:[],
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    addToHistory = (title) => {
        const newHistory = {
            id: uuidv4(),
            title: title
        }
        this.setState({
            history: [...this.state.history, newHistory],
            // text: this.state.text.concat(text)//this.state.text + this.state.completePrompt + " " + title + "\n"
        })
    }

    addCommand = (text) => {
        const newCommand = {
            id: uuidv4(),
            text: text
        }
        this.setState({
            command: [...this.state.command, newCommand],
        })
        for(var i=0;i<this.state.command.length;i++){
        console.log(this.state.command[i].text + " ++++++++++hello+++++++++ ")
        }
    }

    render() {
        return (
            <div className="termContainer">
                <Commands addToHistoryProps={this.addToHistory} 
                prompt={this.state.prompt} 
                path={this.state.path}/>
             {/* <ul>
              {this.state.history.map(his => 
              <li key={uuidv4()}>
                        <span>{his.title}</span>
                    </li>
              )}
            </ul> */}
            </div>
        )
    }
}


export default Terminal
