import React from "react";

import { v4 as uuidv4 } from "uuid"


class Terminal extends React.Component {
    state = {
        completePrompt: "user@localhost:~(#$)",
        title: "",
        history: []
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    addToHistory = title => {
        const newHistory = {
            id: uuidv4(),
            title: title
        }
        this.setState({
            history: [...this.state.history, newHistory]
        })
    }

    listenCmd = event => {
        if (event.key === "Enter") {
            this.addToHistory(this.state.title)
            this.setState()
        }
    }
    render() {
        return (
            <div className="termContainer">
                  <div className="output">

                    </div>
                 <div className="lineCmd">

                <span>
                        {this.state.completePrompt}
                    </span>
                <input type="text" className="inputCmd"
                    value={this.state.title}
                    name="title"
                    onChange={this.onChange}
                    onKeyDown={this.listenCmd}>
                </input>
                </div>
                <ul>
                    {this.state.history.map(
                        his=><li key={his.id}>{his.title}</li>
                    )}
                </ul>
            </div>
        )
    }
}


export default Terminal
