import React from "react";

import { v4 as uuidv4 } from "uuid"
import Commands from "./Commands";

class Terminal extends React.Component {
    state = {
        completePrompt: "user@localhost:~#",
        title: "",
        path: "/home/user",
        history: [],
        text: "",
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    addToHistory = (title,text) => {
        const newHistory = {
            id: uuidv4(),
            title: title
        }
        this.setState({
            history: [...this.state.history, newHistory],
            allHistory: [...this.state.history, newHistory],
            text: this.state.text.concat(text)//this.state.text + this.state.completePrompt + " " + title + "\n"
        })
    }

    render() {
        return (
            <div className="termContainer">
                <Commands />
            </div>
        )
    }
}


export default Terminal




