import React from "react";

import axios from "axios";
import QuestionItem from "./QuestionItem"
import AnswerListItem from "./AnswerListItem"

class QuestionListItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            quest: [],
        };
    }


    async componentDidMount() {
        const QUIZ_API_BASE_URL = "http://localhost:8080/api/quiz/" + `${this.props.id}` + "/question";
        const response = await axios.get(QUIZ_API_BASE_URL);
        this.setState({ quest: response.data });
        console.log(response.data)
    }

    render() {

        return (
            <ul>
                {this.state.quest.map(quiz => (
                    //   <span style={{color: "#53D632"}}>{cmd.text.includes("#")  ? cmd.text.split("#")[0] +"# " : cmd.text.split("$")[0]+"$ " } <a style={{color: "#D7DEDE"}}>{cmd.text.includes("#")  ? cmd.text.split("#")[1] : cmd.text.split("$")[1] }</a></span>  :
                    //   <span style={{color: "#D7DEDE"}}>{cmd.text}</span>}
            <div className="container">
                <span>{this.state.quest[0].text === quiz.text}
                 <QuestionItem
                    key={quiz.id}
                    quest={quiz} /> 
                 <AnswerListItem id={this.state.quest[0].id} /> 
                 </span>
            </div>
                ))}
            </ul>
        )
    }
}

export default QuestionListItem
