import React from "react";

import { v4 as uuidv4 } from "uuid"
import Commands from "./Commands";

class Terminal extends React.Component {
    state = {
        prompt: "user@localhost:~#",
        path: "/home/user",
        command: [{
            id:uuidv4(),
            text: ""
        }],
        history: []
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    addToHistory = (title) => {
        const newHistory = {
            id: uuidv4(),
            title: title
        }
        this.setState({
            history: [...this.state.history, newHistory],
        })
        // this.state.history.map(cmd => {
        //     console.log(cmd.title + " ++++++++++hello+++++++++ ")
        // }
        // )
    }

    addCommand = (text) => {
        const newCommand = {
            id: uuidv4(),
            text:text
        }

        // for(var i=0;i<text.length){
        //     const newElem ={
        //         id: uuidv4(),
        //         text:t
        //     }
        //     newCommand = [...newCommand, newElem]
        // }

            console.log(text.split("\n"))  

        this.setState({
            command: [...this.state.command, newCommand],
        })
    }

    render() {
        return (
            <div className="termContainer">
                <div className="output">
                    <ul>
                        {this.state.command.map(cmd =>
                            <li key={cmd.id}>
                                <span>{cmd.text}</span>
                            </li>
                        )}
                    </ul>
                </div>
                <Commands
                    command={this.state.command}
                    addHistoryProps={this.addToHistory}
                    addCommandPops={this.addCommand}
                    prompt={this.state.prompt}
                    path={this.state.path} />
                <ul>
                    {this.state.history.map(cmd =>
                        <li key={cmd.id}>
                            <span>{cmd.text}</span>
                        </li>
                    )}
                </ul>
            </div>
        )
    }
}


export default Terminal
