import React from "react";

import { v4 as uuidv4 } from "uuid"


class Terminal extends React.Component {
    state = {
        completePrompt:"user@localhost:~(#$)",
        title: "",
        history: []
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    addToHistory = title => {
        const newHistory = {
            id: uuidv4(),
            title: title
        }
        this.setState({
            history: [...this.state.history, newHistory]
        })
    }
    handleSubmit = e => {
        e.preventDefault();
        if (this.state.title.trim()) {
            this.addToHistory(this.state.title)
            this.setState({
                title: "",
            })
        }
    }

    render() {
        return (
            <div className="termContainer">
                <div className="inputLine">
                    <form onSubmit={this.handleSubmit} className="cmdLine">
                        <p>{this.state.completePrompt}</p>
                        <input type="text" className="inputCmd"
                            value={this.state.title}
                            name="title"
                            onChange={this.onChange}></input>
                    </form>
                </div>
                <div className="output">
                    <ul>
                        {this.state.history.map(
                            his => <li><a>{his.title}</a></li>
                        )}
                    </ul>
                </div>
            </div>
        )
    }
}


export default Terminal
